                    <img src="https://theuniqueacademy.co.in/assets/images/test.png" alt="" style={{ width: '100%' }} />
                </div>

                <div className="col-md-6 my-auto">

                    <h1 className='text-center mb-3'>LOGIN FORM</h1>

                    <Formik
                        initialValues={{ email: "", password: "" }}
